import React, { Component } from 'react';
import './App.css';
import contacts from './data/contacts.json';
import Details from './components/Details';
import AddRandom from './components/AddRandom'

class App extends Component {

  state = {
    contactsList: contacts.slice(0, 5)
  }

  handleClick = () => {
    const randomContact = contacts[Math.floor(Math.random() * contacts.length)];
        this.setState({
            contactsList: [...this.state.contactsList, randomContact]
        })
}

  renderContacts() {
    return this.state.contactsList.map((contact, index) => {
      return <Details
      key={index}
      pictureUrl={contact.pictureUrl}
      name={contact.name}
      popularity={contact.popularity}
      />
    })
  }

  render() {
    return (
      <div className="App">
        <h1>Ironcontacts</h1>
        <AddRandom />
        <div className="wrapper">
          <h2 className="unit-titles">Image</h2>
          <h2 className="unit-titles">Name</h2>
          <h2 className="unit-titles">Popularity</h2>
          {this.renderContacts()}
        </div>
      </div>
    )
  }
}

export default App;
