import React, { Component } from 'react';
import './App.css';
import contacts from './data/contacts.json';
import Details from './components/Details';

class App extends Component {

  state = {
    contactsList: contacts.slice(0, 5)
  }

  renderContacts() {
    return this.state.contactsList.map((contact, index) => {
      return <Details
      key={index}
      pictureUrl={contact.pictureUrl}
      name={contact.name}
      popularity={contact.popularity}
      />
    })
  }

  addRandom = () => {
    const randomContact = contacts[Math.floor(Math.random() * contacts.length)];
        this.setState({
            contactsList: [...this.state.contactsList, randomContact]
        })
  }

  sortByName = () => {
    this.setState({
      contactsList : [...state.contactsList.sort()]
    })
  }

  sortByPop = () => {
    console.log ('doble joder');
  }

  render() {
    return (
      <div className="App">
        <h1>Ironcontacts</h1>
        <div className="content">
        <button onClick={this.addRandom}>Add random actor</button>
        <button onClick={this.sortByName}>Sort by name</button>
        <button onClick={this.sortByPop}>Sort by popularity</button>
          <div className="wrapper">
            <h2 className="unit-titles">Image</h2>
            <h2 className="unit-titles">Name</h2>
            <h2 className="unit-titles">Popularity</h2>
            {this.renderContacts()}
          </div>
        </div>
      </div>
    )
  }
}

export default App;
