import React, { Component } from 'react';


class AddRandom extends Component {

    handleClick = () => {
        const {contactsList} = this.state
        this.props.onClick(contactList);
        this.setState({
            contactsList: '',
        })
    }

    render() {
        return (
            <>
                <button onClick={this.handleClick}>Add random actor</button>
            </>
        )
    }
}

export default AddRandom;
